var countDownDate=new Date("June 21, 2017 00:00:00").getTime(),countdownTimer=document.getElementById("countdown-timer"),x=setInterval(function(){var e=(new Date).getTime(),n=countDownDate-e,t=Math.floor(n/864e5),o=Math.floor(n%864e5/36e5),r=Math.floor(n%36e5/6e4),a=Math.floor(n%6e4/1e3);countdownTimer.getElementsByClassName("days-number")[0].innerHTML=t,countdownTimer.getElementsByClassName("hours-number")[0].innerHTML=o,countdownTimer.getElementsByClassName("minutes-number")[0].innerHTML=r,countdownTimer.getElementsByClassName("seconds-number")[0].innerHTML=a,n<0&&(clearInterval(x),countdownTimer.innerHTML="MXM launches today!")},1e3);